import React, { createRef, useRef } from 'react';

import BluetoothLE from '@ionic-native/bluetooth-le';
import BluetoothSerial from '@ionic-native/bluetooth-serial';

import BluetoothDeviceItem from './BluetoothDeviceItem';

const _Bluetooth = () => {

	let states: {} = {};
	let deviceRefs: {} = {};
	const deviceItems: any[] = [];

	const bluetoothInitialize = async () => {
		await this.props.ble.initialize( {"request": true, "statusReceiver": false, "restoreKey": "magic-light-ble"} ).subscribe((result) => {
			if(result.status === "disabled") {
				this.checkEnabled();
			}
		});
	}

	const checkEnabled = () => {
		//this.props.ble.isEnabled().then(isEnabled); //is bluetooth le enabled
		//this.props.ble.isEnabled().then(resp => !resp.isEnabled ? this.setState({isBluetoothEnabled: false}) : this.setState({isBluetoothEnabled: true}) );
	}

	const connect = (id) => {
		deviceItems.forEach(item => { //disconnecting any other currently connected devices and changing their respective states
			if(id === item.props.mac) {
				updateDeviceItemState(item.props.mac, 'connecting');
			}

			if ((item.props.state() === 'connecting' || item.props.state() === 'connected') && item.props.mac !== id) {
				updateDeviceItemState(item.props.mac, 'not_connected');
				deviceRefs[item.props.mac].current.rerender();
			}

		});
	}

	const createDeviceItem = (data) => {
		states = {...states, [data.mac]: data.connected ? 'connected' : 'not_connected'}
		deviceRefs = {...deviceRefs, [data.mac]: createRef<BluetoothDeviceItem>()};
		deviceItems.push(<BluetoothDeviceItem ref={deviceRefs[data.mac]} key={data.mac} mac={data.mac} name={data.name} onClick={(id) => connect(id)} state={() => {return states[data.mac]}}></BluetoothDeviceItem>);
	}
	const updateDeviceItemState = (id, connectingState) => {
		states[id] = connectingState;
	}
	const timeout = (s) => {
		return new Promise(resolve => setTimeout(resolve, s * 1000));
	}

	return (
		{deviceItems, bluetoothInitialize}
	);
}
export default _Bluetooth;



