import React, { useState, useEffect, createContext } from 'react';

import BluetoothLE from '@ionic-native/bluetooth-le';
import BluetoothSerial from '@ionic-native/bluetooth-serial';

import BluetoothDeviceItem from './BluetoothDeviceItem'

const _Bluetooth = () => {

	const state: {found: any[]} = {found: []}
	const devices: any[] = [];

	const bluetoothInitialize = () => {

		for (let i = 0; i < 5; i++) {
			let a = {name: "test", mac: i.toString(), connected:false}
			state.found = [...state.found, a];
			createDeviceItem();
		}
		console.log(state.found);
	}

	const connect = (id) => {
		const currentDevices = Array.prototype.slice.call(devices);

		devices.forEach(item => { //disconnecting any other currently connected devices and updating them in the list (setting them back to not connected)
			console.log(item.props);
			if(id === item.props.mac) {
				updateDeviceItemState(currentDevices.findIndex(function(data){ return data.id === id }), 'connecting');
			}

			if ((item.props.connectingState === 'connecting' || item.props.connectingState === 'connected') && item.props.mac !== id) {
				let indexes = devices.map((elm, idx) => elm.props.mac !== id ? idx: '').filter(String);

				indexes.forEach(index => {
					updateDeviceItemState(index, 'not_connected');
				});
			}

		});
		// bluetoothDevices.forEach(element => {

		// 	console.log(element);

			// if ((element.props.connectingState === 'connecting' || element.props.connectingState === 'connected') && element.props.mac !== id) {
			// 	let updated = <BluetoothDeviceItem key={element.props.mac} mac={element.props.mac} name={element.props.name} onClick={this.connect} connectingState='not_connected'></BluetoothDeviceItem>
			// 	let indexes = bluetoothDevices.map((elm, idx) => elm.props.mac !== id ? idx: '').filter(String);

			// 	indexes.forEach(indx => {
			// 		bluetoothDevices[indx] = updated;
			// 	});
		// 	}


		// 	//let updated = <BluetoothDeviceItem key={a.mac} mac={a.mac} name={a.name} onClick={this.connect} connectingState={a.connected ? 'connected' : 'not_connected'}></BluetoothDeviceItem>

		// 	//updating the clicked device to connecting state

		// 	console.log(element);

		// });
	}

	const createDeviceItem = () => {
		console.log(state.found);
		let d = state.found[state.found.length - 1]
		devices.push(<BluetoothDeviceItem key={d.mac} mac={d.mac} name={d.name} onClick={(id) => connect(id)} connectingState={d.connected ? 'connected' : 'not_connected'}></BluetoothDeviceItem>);
	}
	const updateDeviceItemState = (index, connectingState) => {
		let d = state.found[index];
		devices[index] = <BluetoothDeviceItem key={d.mac} mac={d.mac} name={d.name} onClick={(id) => connect(id)} connectingState={connectingState}></BluetoothDeviceItem>;
	}

	return (
		{devices, bluetoothInitialize, connect}
	);
}

export default _Bluetooth;


