import React, { createRef } from 'react';
import { 
	IonHeader, 
	IonToolbar, 
	IonTitle, 
	IonContent, 
	IonList, 
	IonIcon, 
	IonTabs, 
	IonTabBar, 
	IonTabButton, 
	IonRouterOutlet, 
	IonLabel, 
	IonSpinner, 
	IonFooter, 
} from '@ionic/react';
import { 
	chevronForward, 
	wifiOutline, 
	bluetoothOutline 
} from 'ionicons/icons';

import './index.css';

import { Bluetooth } from '../../../globals/globals';

class Main extends React.Component<{}, {}> {
	_: any;
	ref: any;
	updatekey: number;

	constructor(p) {
		super(p);
		this._ = Bluetooth;
		this.ref = createRef();

		this.updatekey = 1;

		this.refresh = this.refresh.bind(this);
	}


	componentDidMount() {
		this._.bluetoothInitialize();
	}

	render() {
		return (
			<IonContent class="main-page" scrollY={false}>
				<IonHeader>
					<IonToolbar class="main-toolbar">
						<IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
						<IonIcon icon={chevronForward} class="next-button" slot="end"></IonIcon>
					</IonToolbar>
				</IonHeader>
				<IonSpinner class="single-refresh-spinner"></IonSpinner>

				<IonContent class="mid-content">
					<IonList class="device-list" ref={this.ref} key={this.updatekey} onClick={() => {this.updatekey = this.updatekey + 1}}>
						{
							this._.devices.map(function(el) {
								console.log(el);
								return el;
							})
						}
					</IonList>
					{/* <BluetoothDeviceList {...this._}></BluetoothDeviceList> */}
				</IonContent>

				<IonFooter class="footer">
					<IonToolbar class="footer-toolbar">
						<IonTabs>
							<IonRouterOutlet></IonRouterOutlet>
							<IonTabBar slot="bottom" class="tab-bar">
								<IonTabButton tab="Wifi" href="/scan-devices/wifi" disabled={true}>
									<IonIcon icon={wifiOutline} class="button-wifi"></IonIcon> 
									<IonLabel class="button-wifi button-wifi-label">Wifi</IonLabel>
								</IonTabButton>
								<IonTabButton tab="Bluetooth" href="/scan-devices/bluetooth">
									<IonIcon icon={bluetoothOutline} class="button-bluetooth"></IonIcon>
									<IonLabel class="button-bluetooth button-bluetooth-label">Bluetooth</IonLabel>

								</IonTabButton>
							</IonTabBar>
						</IonTabs>
					</IonToolbar>
				</IonFooter>
			</IonContent>
		);
	};

	refresh() {
		//console.log(this._.devices)
		this.ref.current.forceUpdate();
	}
};
export default Main;
