import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonItem, IonIcon, IonAlert, IonTabs, IonTabBar, IonTabButton, IonRouterOutlet, IonLabel, IonRefresher, IonRefresherContent, IonSpinner, IonFooter} from '@ionic/react';
import { chevronForward, wifiOutline, bluetoothOutline } from 'ionicons/icons';

import BluetoothDeviceItem from '../../../components/bluetooth/BluetoothDeviceItem';
import { BluetoothDevice } from '../../../interfaces/BluetoothDevice';

import './index.css';

import { BluetoothLE } from '@ionic-native/bluetooth-le/ngx';

class Main extends React.Component {

  componentDidMount() {
    scanForDevices.scan(1);
  }

  componentWillUnmount() {
  }

  render() {
    return (
      <IonContent class="main-page">

        <IonHeader>
          <IonToolbar class="main-toolbar">
            <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
            <IonIcon icon={chevronForward} class="next-button" slot="end"></IonIcon>
          </IonToolbar>
        </IonHeader>
        <IonSpinner class="single-refresh-spinner"></IonSpinner>

        <IonContent class="mid-content">
          <IonList class="device-list">
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
          </IonList>
          <IonRefresher slot="fixed">
              <IonRefresherContent
                pullingText="Pull to refresh"
                refreshingText="Refreshing...">
              </IonRefresherContent>
          </IonRefresher>
        </IonContent>

        <IonFooter class="main-footer">
          <IonToolbar>
            <IonTabs>
              <IonRouterOutlet></IonRouterOutlet>
              <IonTabBar slot="bottom" class="tab-bar">
                <IonTabButton tab="Wifi" href="/scan-devices/wifi" disabled={true}>
                  <IonIcon icon={wifiOutline} class="button-wifi"></IonIcon> 
                  <IonLabel class="button-wifi button-wifi-label">Wifi</IonLabel>
                </IonTabButton>
                <IonTabButton tab="Bluetooth" href="/scan-devices/bluetooth">
                  <IonIcon icon={bluetoothOutline} class="button-bluetooth"></IonIcon>
                  <IonLabel class="button-bluetooth button-bluetooth-label">Bluetooth</IonLabel>

                </IonTabButton>
              </IonTabBar>
            </IonTabs>
          </IonToolbar>
        </IonFooter>

        {/*<IonAlert
          isOpen={true}
          header={'Continue?'}
          message={'Are you sure you want to continue without selecting a device?'}
          buttons={['Cancel', 'Continue']}
        />*/}

      </IonContent>
    );
  };
};

class scanForDevices {

  static scan(timeout) {
    this.bluetoothScan(timeout);
  }

  private static bluetoothScan(timeout) {
  } 
}


export default Main;
