import React, { useState } from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonButton, IonIcon, IonAlert, IonTabs, IonTabBar, IonTabButton, IonRouterOutlet, IonLabel, IonRefresher, IonRefresherContent, IonSpinner, IonFooter} from '@ionic/react';
import { chevronForward, wifiOutline, bluetoothOutline } from 'ionicons/icons';

import './index.css';

import BluetoothDeviceItem from '../../../components/bluetooth/BluetoothDeviceItem';
import { BluetoothDevice } from '../../../interfaces/BluetoothDevice';

import { isPlatform } from '@ionic/react'
import { BluetoothLE } from '@ionic-native/bluetooth-le';

class Main extends React.Component {

  componentDidMount() {
    let a = new scanForDevices(BluetoothLE);
    a.bluetoothInitialize();
    a.scan(10);
  }

  componentWillUnmount() {
  }

  render() {
    return (
      <IonContent class="main-page">

        <IonHeader>
          <IonToolbar class="main-toolbar">
            <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
            <IonIcon icon={chevronForward} class="next-button" slot="end"></IonIcon>
          </IonToolbar>
        </IonHeader>
        <IonSpinner class="single-refresh-spinner"></IonSpinner>

        <IonContent class="mid-content">
          <IonList class="device-list">
          </IonList>
          <IonRefresher slot="fixed">
              <IonRefresherContent
                pullingText="Pull to refresh"
                refreshingText="Refreshing...">
              </IonRefresherContent>
          </IonRefresher>
        </IonContent>

        <IonFooter class="main-footer">
          <IonToolbar>
            <IonTabs>
              <IonRouterOutlet></IonRouterOutlet>
              <IonTabBar slot="bottom" class="tab-bar">
                <IonTabButton tab="Wifi" href="/scan-devices/wifi" disabled={true}>
                  <IonIcon icon={wifiOutline} class="button-wifi"></IonIcon> 
                  <IonLabel class="button-wifi button-wifi-label">Wifi</IonLabel>
                </IonTabButton>
                <IonTabButton tab="Bluetooth" href="/scan-devices/bluetooth">
                  <IonIcon icon={bluetoothOutline} class="button-bluetooth"></IonIcon>
                  <IonLabel class="button-bluetooth button-bluetooth-label">Bluetooth</IonLabel>

                </IonTabButton>
              </IonTabBar>
            </IonTabs>
          </IonToolbar>
        </IonFooter>
      </IonContent>
    );
  };
};
export default Main;

export const AlertExample: React.FC = () => {

  const [showAlert1, setShowAlert1] = useState(false);
  const [showAlert2, setShowAlert2] = useState(false);
  const [showAlert3, setShowAlert3] = useState(false);
  const [showAlert4, setShowAlert4] = useState(false);
  const [showAlert5, setShowAlert5] = useState(false);
  const [showAlert6, setShowAlert6] = useState(false);

    return (
      <IonContent>
        <IonButton onClick={() => setShowAlert1(true)} expand="block">Show Alert 1</IonButton>
        <IonButton onClick={() => setShowAlert2(true)} expand="block">Show Alert 2</IonButton>
        <IonButton onClick={() => setShowAlert3(true)} expand="block">Show Alert 3</IonButton>
        <IonButton onClick={() => setShowAlert4(true)} expand="block">Show Alert 4</IonButton>
        <IonButton onClick={() => setShowAlert5(true)} expand="block">Show Alert 5</IonButton>
        <IonButton onClick={() => setShowAlert6(true)} expand="block">Show Alert 6</IonButton>
        <IonAlert
          isOpen={showAlert1}
          onDidDismiss={() => setShowAlert1(false)}
          cssClass='my-custom-class'
          header={'Alert'}
          subHeader={'Subtitle'}
          message={'This is an alert message.'}
          buttons={['OK']}
        />

        <IonAlert
          isOpen={showAlert2}
          onDidDismiss={() => setShowAlert2(false)}
          cssClass='my-custom-class'
          header={'Alert'}
          subHeader={'Subtitle'}
          message={'This is an alert message.'}
          buttons={['Cancel', 'Open Modal', 'Delete']}
