class scanForDevices {
  isCordova: boolean;

  constructor(public ble: typeof BluetoothLE) {
    this.ble = ble;
    this.isCordova = true;
   }

   scan = async (timeout) => {
    if(this.isCordova) {
      console.log("scanning!");

      this.ble.startScan( {"allowDuplicates": false, "isConnectable": true}  ).subscribe((result) => {
        if(result.status === "scanResult") {
          this.addDevice(result);
        }
        else if(result.status !== "scanStarted") {
          this.checkEnabled();
          //warn user about not scanning
        }
      });

      await this.timeout(timeout);
      this.ble.stopScan();

      console.log("stopped scan!");
    }
  }

  bluetoothInitialize = async () => {
    if (!isPlatform('cordova')) {
      console.log("Platform not cordova, BLE will not work!");
      this.isCordova = false;
      return;
    }

    this.checkEnabled();

    await this.ble.initialize( {"request": true, "statusReceiver": false, "restoreKey": "magic-light-ble"} ).subscribe((result) => {
      if(result.status === "disabled") {
        this.checkEnabled();
      }
    });
  }

  private checkEnabled() {
    this.ble.isEnabled().then(function(isEnabled) {
      if(!isEnabled.isEnabled) {
        console.log("bluetooth **not** enabled!");
          //tell user about bluetooth (ionalert)
      }
    });
  }

