import React from 'react';
import { IonIcon, IonSpinner, IonItem, IonAvatar, IonLabel, IonItemDivider } from '@ionic/react';
import { bluetoothOutline, checkmarkOutline, closeOutline, ellipsisVertical } from 'ionicons/icons';

import './BluetoothDeviceItem.css';
import { BluetoothDevice } from '../../interfaces/BluetoothDevice'

interface Props {
  device: BluetoothDevice;
}

class BluetoothDeviceItem extends React.Component {

  componentDidMount() {
  }

  render() {
    return (
    <IonItem lines="full" class="item-container">
      <IonAvatar slot="start">
        <IonIcon icon={bluetoothOutline} class="bluetooth-icon"></IonIcon>
      </IonAvatar>
      <IonAvatar slot="end">
        <IonIcon icon={closeOutline} class="connecting-icons"></IonIcon>
      </IonAvatar>
      <IonAvatar slot="end">
        <IonIcon icon={ellipsisVertical} class="group-icons"></IonIcon>
      </IonAvatar>
      <IonLabel>
        <div className="device-name">this-is-a-really-long-device-name-for-some-reason</div>
        <div className="device-mac">00:04:ab:8f:40:hg</div>
      </IonLabel>
    </IonItem>
    );
  }

  conenct() {
    console.log("clicked!");
  }
}

export default BluetoothDeviceItem;


