class scanForDevices {
  initializeParams: { request: boolean; statusReceiver: boolean; restoreKey: string; }; //how can this be cleaned up?
  scanParams: { allowDuplicates: boolean; isConnectable: boolean; _isScalar: boolean};
  isCordova: boolean;

  constructor(public ble: typeof BluetoothLE) {
    this.ble = ble;

    this.initializeParams = {"request": true, "statusReceiver": false, "restoreKey": "magic-light-ble"};
    this.scanParams = {"allowDuplicates": false, "isConnectable": true, "_isScalar": true} ;
    this.isCordova = true;
   }

   scan = async (timeout) => {
    if(this.isCordova) {
      console.log("scanning!");

      this.ble.startScan(this.scanParams).subscribe((result) => {
        if(result.status === "scanStarted") {

        }
        else {
          //this.checkEnabled();
          //warn user about not scan
        }
      });

      await this.timeout(timeout);
      this.ble.stopScan();

      console.log("stopped scan!");
    }
  }

  bluetoothInitialize = async () => {
    if (!isPlatform('cordova')) {
      console.log("Platform not cordova, BLE will not work!");
      this.isCordova = false;
      return;
    }

    this.checkEnabled();

    await this.ble.initialize(this.initializeParams).subscribe((result) => {
      if(result.status === "disabled") {
        this.checkEnabled();
      }
    });
  }

  private checkEnabled() {
    this.ble.isEnabled().then(function(isEnabled) {
      if(!isEnabled) {
        console.log("bluetooth **not** enabled!");
          //tell user about bluetooth (ionalert)
      }
    });
  }

  private timeout(s) {
    return new Promise(resolve => setTimeout(resolve, s*1000));
  } 
}

