import React, { useState, useEffect } from 'react';
import { IonIcon, IonSpinner, IonItem, IonAvatar, IonLabel, isPlatform } from '@ionic/react';
import { bluetoothOutline, checkmarkOutline, closeOutline } from 'ionicons/icons';

import BluetoothLE from '@ionic-native/bluetooth-le';
import BluetoothSerial from '@ionic-native/bluetooth-serial';

import './Bluetooth.css';
import { bluetoothDevices } from '../../globals/globals';


interface Props {
	name: string;
	mac: string;
	connectngState: string;
}



const BluetoothDeviceItem = (Props, { }) => {

	const [connectingState, setConnectingState] = useState(BluetoothDeviceItemProps.connectingState);
	useEffect(() => {
		stateChangeCallback();
	}, [connectingState]);

	const { onClick } = BluetoothDeviceItemProps;

	const stateChangeCallback = () => {
		const elementIndex = Array.prototype.slice.call(document.getElementsByClassName("device-list")[0].children).findIndex(function (data) { return data.id === BluetoothDeviceItemProps.mac })
		const connectingIcon = document.getElementsByClassName("connecting-icons")[elementIndex];
		const spinnerIcon = document.getElementsByClassName("connecting-spinner")[elementIndex]["style"];

		switch (connectingState) {
			case 'not_connected':
				connectingIcon["style"]["color"] = "var(--ion-color-danger)";
				connectingIcon["icon"] = closeOutline;
				break;
			case 'connecting':
				spinnerIcon.opacity = 1;
				connectingIcon["style"]["opacity"] = 0;
				break;
			case 'connected':
				spinnerIcon.opactiy = 0;
				connectingIcon["style"]["opacity"] = 1;
				connectingIcon["style"]["color"] = "var(--ion-color-success)";
				connectingIcon["icon"] = checkmarkOutline;
		}
	}

	const wait = async () => {
		setConnectingState('connecting');

		const ret = await onClick(BluetoothDeviceItemProps.mac);
	}

	return (
		<IonItem lines="none" class="item-container">
			<IonAvatar slot="start" class="bluetooth-icon-container">
				<IonIcon icon={bluetoothOutline} class="bluetooth-icon"></IonIcon>
			</IonAvatar>
			<IonAvatar slot="end" class="connecting-icons-container">
				<IonIcon icon={closeOutline} class="connecting-icons"></IonIcon>
				<IonSpinner name="dots" class="connecting-spinner"></IonSpinner>
			</IonAvatar>
			<IonLabel>
				<div className="device-name"></div>
				<div className="device-mac"></div>
			</IonLabel>
		</IonItem>
	);
}


export default BluetoothDeviceItem;
















