import { createReducer } from "@reduxjs/toolkit";
import { setDevices } from "../actions/BluetoothDevices";

const devices = {
  devices: []
};


export const BluetoothDevicesReducer = createReducer(
  { devices },
  {
    [setDevices.type]: (state, action) => ({
      ...state,
      loader: { ...action.payload }
    }),
  }
);
