"use strict";

import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonIcon, IonAlert, IonTabs, IonTabBar, IonTabButton, IonRouterOutlet, IonLabel, IonRefresher, IonRefresherContent, IonSpinner, IonFooter, IonToast} from '@ionic/react';
import { chevronForward, wifiOutline, bluetoothOutline } from 'ionicons/icons';

import { isPlatform } from '@ionic/react'
import { BluetoothLE, ScanStatus } from '@ionic-native/bluetooth-le';
import { BluetoothSerial } from '@ionic-native/bluetooth-serial';
import { OpenNativeSettings } from '@ionic-native/open-native-settings';

import './index.css';
import BluetoothDeviceItem from '../../../components/bluetooth/BluetoothDeviceItem';

//import BluetoothDeviceItem from '../../../components/bluetooth/BluetoothDeviceItem';
//import { BluetoothDevice } from '../../../interfaces/BluetoothDevice';

interface State {
  isEnabled: boolean;
}


class Main extends React.Component {
  state = {
    isBluetoothEnabled: false,
    isErrorConnecting: false,
    isContinueNoDevice: false,
  }

  componentDidMount() {
    let a = new scanForDevices(BluetoothLE, BluetoothSerial);

    //a.bluetoothInitialize();
    //a.scan(10);
  }

  componentWillUnmount() {

  }

  componentWillMount() {

  }

  render() {
    return (
      <IonContent class="main-page">

        <IonHeader>
          <IonToolbar class="main-toolbar">
            <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
            <IonIcon icon={chevronForward} class="next-button" slot="end"></IonIcon>
          </IonToolbar>
        </IonHeader>
        <IonSpinner class="single-refresh-spinner"></IonSpinner>

        <IonContent class="mid-content">
          <IonList class="device-list">
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
          </IonList>
          <IonRefresher slot="fixed">
              <IonRefresherContent
                pullingText="Pull to refresh"
                refreshingText="Refreshing...">
              </IonRefresherContent>
          </IonRefresher>
        </IonContent>

        <IonFooter class="main-footer">
          <IonToolbar>
            <IonTabs>
              <IonRouterOutlet></IonRouterOutlet>
              <IonTabBar slot="bottom" class="tab-bar">
                <IonTabButton tab="Wifi" href="/scan-devices/wifi" disabled={true}>
                  <IonIcon icon={wifiOutline} class="button-wifi"></IonIcon> 
                  <IonLabel class="button-wifi button-wifi-label">Wifi</IonLabel>
                </IonTabButton>
                <IonTabButton tab="Bluetooth" href="/scan-devices/bluetooth">
                  <IonIcon icon={bluetoothOutline} class="button-bluetooth"></IonIcon>
                  <IonLabel class="button-bluetooth button-bluetooth-label">Bluetooth</IonLabel>

                </IonTabButton>
              </IonTabBar>
            </IonTabs>
          </IonToolbar>
        </IonFooter>
        <IonAlert
          isOpen={this.state.isContinueNoDevice}
          header={'Continue?'}
          message={'Are you sure you want to continue without selecting a device?'}
          buttons={[
            { text: 'Cancel', },
            {
              text: 'Okay',
              handler: () => {
                console.log('continue ok'); //move to next screen
              }
            }
          ]}
        />
        <IonAlert
          isOpen={this.state.isBluetoothEnabled}
          header={'Error'}
          message={'Bluetooth is not enabled. Please enable Bluetooth and try again.'}
          buttons={[
            {
              text: 'Settings',
              handler: () => {
                document.addEventListener("resume", function() {
                  let a = new scanForDevices(BluetoothLE, BluetoothSerial);
                  a.bluetoothInitialize();
                  a.scan(10);
                }, false);
                console.log('opening settings');
                OpenNativeSettings.open("bluetooth");
              }
            },
            {
              text: 'Okay',
              handler: () => {
                let a = new scanForDevices(BluetoothLE, BluetoothSerial);
                a.bluetoothInitialize();
                a.scan(10);
              }
            }
          ]
        }
        />
        <IonAlert
          isOpen={this.state.isErrorConnecting}
          header={'Error'}
          message={'There was an error connecting to the Bluetooth device. Make sure the device is turned on and try again.'}
          buttons={['OK']}
        />
      </IonContent>
    );
  };
};
export default Main;

