"use strict";

import React from 'react';
import { IonIcon, IonSpinner, IonItem, IonAvatar, IonLabel } from '@ionic/react';
import { bluetoothOutline, checkmarkOutline, closeOutline } from 'ionicons/icons';

import './BluetoothDeviceItem.css';
import { BluetoothDevice } from '../../interfaces/BluetoothDevice'

interface Props {
  id: string
  name: string;
  mac: string;
}

class BluetoothDeviceItem extends React.Component<Props, {}> {

  constructor(props) {
    super(props);
  }

  componentDidMount() {
  }

  render() {
    return (
    <IonItem lines="none" class="item-container" onClick={() => this.connenct()} id={this.props.id}>
      <IonAvatar slot="start" class="bluetooth-icon-container">
        <IonIcon icon={bluetoothOutline} class="bluetooth-icon"></IonIcon>
      </IonAvatar>
      <IonAvatar slot="end" class="connecting-icons-container">
        <IonIcon icon={closeOutline} class="connecting-icons"></IonIcon>
        <IonSpinner name="dots" class="connecting-spinner"></IonSpinner>
      </IonAvatar>
      <IonLabel>
        <div className="device-name">{this.props.name}</div>
        <div className="device-mac">{this.props.mac}</div>
      </IonLabel>
    </IonItem>
    );
  }

  private connenct = async () => {

    const ic = document.getElementsByClassName("connecting-icons")[0];
    const sp = document.getElementsByClassName("connecting-spinner")[0];

    sp["style"]["opacity"] = 1;
    ic["style"]["opacity"] = 0;

    try { await this.tryConnect(); } 
    catch(error) { this.switchIcon(false, sp, ic); }

    this.switchIcon(true, sp, ic);

  }

  private tryConnect() {
    return new Promise(function(resolve, reject) {
      console.log("connecting...");
      setTimeout(function(){ resolve(1); }, 1000);
    });
  }

  private switchIcon(connected, sp, ic) {
    sp["style"]["opacity"] = 0;
    ic["style"]["opacity"] = 1;
    ic["style"]["color"] = connected ? "var(--ion-color-success)" : "var(--ion-color-danger)";
    ic["icon"] = connected ? checkmarkOutline : closeOutline;
  }
}

export default BluetoothDeviceItem;
