		for (let i = 0; i < 5; i++) {
			let a = {name: "test", mac: i.toString(), connected:false}
			devices.found = [...devices.found, a];
			createDeviceItem();
		}
	}

	const connect = (id) => {
		deviceItems.forEach(item => { //disconnecting any other currently connected devices and updating them in the list (setting them back to not connected)
