				updateDeviceItemState(deviceItems.findIndex(function(data){ return data.props.mac === id }), 'connecting');
			}


			if ((item.props.connectingState === 'connecting' || item.props.connectingState === 'connected') && item.props.mac !== id) {
				let indexes = deviceItems.map((elm, idx) => elm.props.mac !== id ? idx: '').filter(String);

				indexes.forEach(index => {
					updateDeviceItemState(index, 'not_connected');
				});
			}

		});
	}

	const createDeviceItem = () => {
		let d = devices.found[devices.found.length - 1]
		console.log({...states});
		states = {[d.mac]: d.connected ? 'connected' : 'not_connected'}
		console.log(states);
		//deviceItems.push(<BluetoothDeviceItem key={d.mac} mac={d.mac} name={d.name} onClick={(id) => connect(id)} setState={() => {return states[states.length - 1]}}></BluetoothDeviceItem>);
	}
	const updateDeviceItemState = (index, connectingState) => {
		let d = devices.found[index];
		states[index] = 'connecting'; //<BluetoothDeviceItem key={d.mac} mac={d.mac} name={d.name} onClick={(id) => connect(id)} connectingState={connectingState}></BluetoothDeviceItem>;
		console.log(states)
		console.log(deviceItems[index].props.setState());
	}

	return (
		{deviceItems, bluetoothInitialize, connect}
	);
}
