import React from 'react';
import { 
	IonHeader, 
	IonToolbar, 
	IonTitle, 
	IonContent, 
	IonList, 
	IonIcon, 
	IonAlert, 
	IonTabs, 
	IonTabBar, 
	IonTabButton, 
	IonRouterOutlet, 
	IonLabel, 
	IonSpinner, 
	IonFooter, 
	isPlatform, 
	IonItem
} from '@ionic/react';
import { 
	chevronForward, 
	wifiOutline, 
	bluetoothOutline 
} from 'ionicons/icons';

import './index.css';

import BluetoothDeviceItem from '../../../components/bluetooth/Bluetooth'
import { BluetoothLE } from '@ionic-native/bluetooth-le';
import { bluetoothDevices } from '../../../globals/globals';

const devices: any[] = [];

interface Props {
	bls: Object;
	ble: Object;
}

class Main extends React.Component<{}, {}> {
	a = new Bluetooth({ bls: "a", ble: "a" });

	componentDidMount() {
		this.a.bluetoothInitialize();
	}

	render() {
		return (
			<IonContent class="main-page" scrollY={false}>

				<IonHeader>
					<IonToolbar class="main-toolbar">
						<IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
						<IonIcon icon={chevronForward} class="next-button" slot="end"></IonIcon>
					</IonToolbar>
				</IonHeader>
				<IonSpinner class="single-refresh-spinner"></IonSpinner>

				<IonContent class="mid-content">
					<IonList class="device-list">
						{
							bluetoothDevices.map(el => el)
						}
					</IonList>
				</IonContent>

				<IonFooter class="footer">
					<IonToolbar class="footer-toolbar">
						<IonTabs>
							<IonRouterOutlet></IonRouterOutlet>
							<IonTabBar slot="bottom" class="tab-bar">
								<IonTabButton tab="Wifi" href="/scan-devices/wifi" disabled={true}>
									<IonIcon icon={wifiOutline} class="button-wifi"></IonIcon> 
									<IonLabel class="button-wifi button-wifi-label">Wifi</IonLabel>
								</IonTabButton>
								<IonTabButton tab="Bluetooth" href="/scan-devices/bluetooth">
									<IonIcon icon={bluetoothOutline} class="button-bluetooth"></IonIcon>
									<IonLabel class="button-bluetooth button-bluetooth-label">Bluetooth</IonLabel>

								</IonTabButton>
							</IonTabBar>
						</IonTabs>
					</IonToolbar>
				</IonFooter>
			</IonContent>
		);
	};
};
export default Main;


class Bluetooth extends React.Component<Props, {}> {

	constructor(Props) {
		super(Props);
	}

	bluetoothInitialize = () => {

		for (let i = 0; i < 5; i++) {
			let a = {name: "test", mac: i.toString(), connected:false}
			devices.push(a);
			bluetoothDevices.push(<BluetoothDeviceItem key={a.mac} mac={a.mac} name={a.name} onClick={(id) => this.connect(id)} connectingState={a.connected ? 'connected' : 'not_connected'}></BluetoothDeviceItem>)
		}
	}

	connect = (id) => {
		const currentDevices = Array.prototype.slice.call(document.getElementsByClassName("device-list")[0].children);

		devices.forEach(item => { //disconnecting any other currently connected devices and updating them in the list (setting them back to not connected)

			if(id === item.mac) {
				let updated = <BluetoothDeviceItem key={item.mac} mac={item.mac} name={item.name} onClick={this.connect} connectingState={'connecting'}></BluetoothDeviceItem>	
				bluetoothDevices[currentDevices.findIndex(function(data){ return data.id === id })] = updated;
			}

		});

		bluetoothDevices.forEach(element => {

			console.log(element);

			if ((element.props.connectingState === 'connecting' || element.props.connectingState === 'connected') && element.props.mac !== id) {
				let updated = <BluetoothDeviceItem key={element.props.mac} mac={element.props.mac} name={"hf"} onClick={this.connect} connectingState='not_connected'></BluetoothDeviceItem>

				bluetoothDevices[currentDevices.findIndex(function(data){ return data.id === id })] = updated;
			}


			//let updated = <BluetoothDeviceItem key={a.mac} mac={a.mac} name={a.name} onClick={this.connect} connectingState={a.connected ? 'connected' : 'not_connected'}></BluetoothDeviceItem>

			//updating the clicked device to connecting state


		});
	}
}
