import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonItem, IonIcon, IonAlert, IonTabs, IonTabBar, IonTabButton, IonRouterOutlet, IonLabel, IonRefresher, IonRefresherContent} from '@ionic/react';
import { refresh, chevronForward, wifiOutline, bluetoothOutline, arrowForwardSharp} from 'ionicons/icons';
import { RefresherEventDetail } from '@ionic/core';

import BluetoothDeviceItem from '../../../components/bluetooth/BluetoothDeviceItem';
import { BluetoothDevice } from '../../../interfaces/BluetoothDevice';

import './index.css';
import { connected } from 'process';

interface State {
  device: BluetoothDevice;
  deviceList: BluetoothDevice[];
}

class Main extends React.Component {

  componentDidMount() {
    scanForDevices.scan();
  }

  componentWillUnmount() {
  }

  render() {
    return (
      <IonContent class="main-page">
        <IonContent class="header-content">
          <IonHeader class="main-header">
            <IonToolbar class="main-toolbar">
              <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
              <IonIcon icon={arrowForwardSharp} class="next-button" slot="end"></IonIcon>
            </IonToolbar>
          </IonHeader>
        </IonContent>
        <IonContent class="mid-content">
          <IonList class="device-list">
            <IonItem>TestItem</IonItem>
          </IonList>
          <IonRefresher onIonRefresh={scanForDevices.scan} slot="fixed">
              <IonRefresherContent
                pullingText="Pull to refresh"
                refreshingSpinner="circles"
                refreshingText="Refreshing...">
              </IonRefresherContent>
          </IonRefresher>
        </IonContent>
        <IonTabs>
          <IonRouterOutlet></IonRouterOutlet>
          <IonTabBar slot="bottom" class="tab-bar">
            <IonTabButton tab="Wifi">
              <IonIcon icon={wifiOutline} class="button-wifi"></IonIcon> 
              <IonLabel class="button-wifi">Wifi</IonLabel>
              <IonContent class="button-wifi-background"></IonContent>
            </IonTabButton>
            <IonTabButton tab="Bluetooth"href="/scan-devices/bluetooth">
              <IonIcon icon={bluetoothOutline} class="button-bluetooth"></IonIcon>
              <IonLabel class="button-bluetooth">Bluetooth</IonLabel>
              <IonContent class="button-bluetooth-background"></IonContent>
            </IonTabButton>
          </IonTabBar>
        </IonTabs>
        <IonAlert
          isOpen={showAlert1}
          onDidDismiss={() => setShowAlert1(false)}
          cssClass='my-custom-class'
          header={'Alert'}
          subHeader={'Subtitle'}
          message={'This is an alert message.'}
          buttons={['OK']}
        />
      </IonContent>
    );
  };
};
