import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonItem, IonIcon, IonAlert, IonTabs, IonTabBar, IonTabButton, IonRouterOutlet, IonLabel, IonRefresher, IonRefresherContent, IonSpinner, IonFooter} from '@ionic/react';
import { chevronForward, wifiOutline, bluetoothOutline } from 'ionicons/icons';


import './index.css';

import BluetoothDeviceItem from '../../../components/bluetooth/BluetoothDeviceItem';
import { BluetoothDevice } from '../../../interfaces/BluetoothDevice';

import { isPlatform } from '@ionic/react'
import { BluetoothLE } from '@ionic-native/bluetooth-le';

class Main extends React.Component {

  componentDidMount() {
    let a = new scanForDevices(BluetoothLE);
    a.bluetoothInitialize();
    a.scan(5);
  }

  componentWillUnmount() {
  }

  render() {
    return (
      <IonContent class="main-page">

        <IonHeader>
          <IonToolbar class="main-toolbar">
            <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
            <IonIcon icon={chevronForward} class="next-button" slot="end"></IonIcon>
          </IonToolbar>
        </IonHeader>
        <IonSpinner class="single-refresh-spinner"></IonSpinner>

        <IonContent class="mid-content">
          <IonList class="device-list">
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
            <BluetoothDeviceItem></BluetoothDeviceItem>
          </IonList>
          <IonRefresher slot="fixed">
              <IonRefresherContent
                pullingText="Pull to refresh"
                refreshingText="Refreshing...">
              </IonRefresherContent>
          </IonRefresher>
        </IonContent>

        <IonFooter class="main-footer">
          <IonToolbar>
            <IonTabs>
              <IonRouterOutlet></IonRouterOutlet>
              <IonTabBar slot="bottom" class="tab-bar">
                <IonTabButton tab="Wifi" href="/scan-devices/wifi" disabled={true}>
                  <IonIcon icon={wifiOutline} class="button-wifi"></IonIcon> 
                  <IonLabel class="button-wifi button-wifi-label">Wifi</IonLabel>
                </IonTabButton>
                <IonTabButton tab="Bluetooth" href="/scan-devices/bluetooth">
                  <IonIcon icon={bluetoothOutline} class="button-bluetooth"></IonIcon>
                  <IonLabel class="button-bluetooth button-bluetooth-label">Bluetooth</IonLabel>

                </IonTabButton>
              </IonTabBar>
            </IonTabs>
          </IonToolbar>
        </IonFooter>

        {/*<IonAlert
          isOpen={true}
          header={'Continue?'}
          message={'Are you sure you want to continue without selecting a device?'}
          buttons={['Cancel', 'Continue']}
        />*/}
        {/*<IonAlert
          isOpen={true}
          header={'Try Again'}
          message={'Bluetooth is not enabled. Please enable Bluetooth and try again.'}
          buttons={['Refresh', 'Settings']}
        />*/}
        {/*<IonAlert
          isOpen={true}
          header={'Error'}
          message={'There was an error connecting to the Bluetooth device. Make sure you are selecting the right device and try again.'}
          buttons={['OK']}
        />*/}

      </IonContent>
    );
  };
};

class scanForDevices {
  initializeParams: { request: boolean; statusReceiver: boolean; restoreKey: string; };
  scanParams: { allowDuplicates: boolean; isConnectable: boolean; };
  devices: object;
  isCordova: boolean;

  constructor(public ble: typeof BluetoothLE) {
    this.ble = ble;

    this.initializeParams = {"request": true, "statusReceiver": false, "restoreKey": "magic-light-ble"};
    this.scanParams = {"allowDuplicates": false, "isConnectable": true};
    this.devices = [];
    this.isCordova = true;
   }

  scan(timeout) {
    if(this.isCordova) {
      console.log("connecting!");

      this.devices = this.ble.startScan(this.scanParams); 
      setTimeout(() => { 
        this.ble.stopScan();
      }, timeout*1000);
    }
  }  

  bluetoothInitialize() {
    if (!isPlatform('cordova')) {
      console.log("Platform not cordova, BLE will not work!");
      this.isCordova = false;
      return;
    }

    this.checkPermissionsAndroid();

    let initializeResult = this.ble.initialize(this.initializeParams);
    if(initializeResult["status"] == "disabled") {
      //Alert for bluetooth disabled. Ask to scan again / open settings.
    }
  }

  checkPermissionsAndroid() {
    if(isPlatform('android')) {
      let per = this.ble.hasPermission();
      let loc = this.ble.isLocationEnabled();

      if(!per["hasPermission"]) {
        this.ble.requestPermission();
      }
      if(!loc["requestLocation"]) {
        this.ble.requestLocation();
      }
    }
  }
}

///Implement checks to see if bluetooth is enabled, bluetooth has permissions (android) and so on.
//https://github.com/randdusing/cordova-plugin-bluetoothle#permissions-android
//Bad formatting but good info on androind permissions: https://ionicframework.com/docs/v3/native/bluetoothle/


export default Main;
