import React, { createRef, useRef } from 'react';

import { BluetoothLE as ble } from '@ionic-native/bluetooth-le';
import { BluetoothSerial as bls } from '@ionic-native/bluetooth-serial';

import BluetoothDeviceItem from './BluetoothDeviceItem';
import { isPlatform } from '@ionic/react';

const _Bluetooth = () => {

	let states: {} = {};
	let deviceRefs: {} = {};
	const deviceItems: any[] = [];

	const serviceUuids: string[] = [];

	const bluetoothInitialize = async () => {
		await ble.initialize( {"request": true, "statusReceiver": false, "restoreKey": "magic-light-ble"} ).subscribe((result) => {
			if(result.status === "disabled") {
				checkEnabled();
			}
		});
	}

	const scan = async (t) => {
		//ble.stopScan(); //stop any current scans

		console.log("scanning!");

		// ble.startScan( {"allowDuplicates": false, "isConnectable": true}  ).subscribe((result) => {
		// 	if(result.status === "scanResult") {
		// 		createDeviceItem(result, true);
		// 	}
		// 	else if(result.status !== "scanStarted") {
		// 		checkEnabled();
		// 	}
		// });
		// ble.retrieveConnected({services: serviceUuids}).then((result) => {
		// 	createDeviceItem(result, false);
		// })

		await timeout(t);
		//ble.stopScan();

		console.log("stopped scan!");
	}

	const connect = (id) => {
		updateDeviceItemState(id, 'connecting');
		checkAlreadyConnecting(id);
	}

	const checkAlreadyConnecting = (id) => {
		deviceItems.forEach(item => { //disconnecting any other currently connected devices and changing their respective states
			if ((item.props.state() === 'connecting' || item.props.state() === 'connected') && item.props.mac !== id) {
				updateDeviceItemState(item.props.mac, 'not_connected');
				deviceRefs[item.props.mac].current.rerender();

				ble.disconnect({address: id});
				ble.close({address: id});
			}
		});
	}

	const createDeviceItem = (data, isFromUnpaired) => {
		if(states[data.address] === ""){
			return; //device already added to list
		}
		if(isPlatform("ios") && isFromUnpaired) {
			serviceUuids.push(...data.advertisement.serviceUuids);
			states = {...states, [data.address]: data.advertisement.isConnectable ? 'not_connected' : 'connected'};
		}
		else {
			states = {...states, [data.address]: 'not_connected'};
		}

		deviceRefs = {...deviceRefs, [data.address]: createRef<BluetoothDeviceItem>()};
		deviceItems.push(<BluetoothDeviceItem ref={deviceRefs[data.address]} mac={data.address} name={data.name} onClick={(id) => connect(id)} state={() => {return states[data.address]}}></BluetoothDeviceItem>);
	}

	const updateDeviceItemState = (id, connectingState) => {
		states[id] = connectingState;
	}
	const checkEnabled = () => {
		//ble.isEnabled().then(isEnabled); //is bluetooth le enabled
		//ble.isEnabled().then(resp => !resp.isEnabled ? this.setState({isBluetoothEnabled: false}) : this.setState({isBluetoothEnabled: true}) );
	}
	const timeout = (s) => {
		return new Promise(resolve => setTimeout(resolve, s * 1000));
	}

	return (
		{deviceItems, bluetoothInitialize, scan}
	);
}
export default _Bluetooth;



