		return false;
	}

	async scan(): Promise<any[]> {
		return [];
	}

	timeouts(s): Promise<object> {
		return new Promise(resolve => setTimeout(resolve, s * 1000));
	}

	onResume(): boolean {
		console.log("resumed!");
		return this.initialize()
	}
}

