import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonItem, IonIcon, IonSpinner, IonTabs, IonTabBar, IonTabButton, IonRouterOutlet} from '@ionic/react';
import { refresh, chevronForward, wifiOutline } from 'ionicons/icons';

import BluetoothDevice from '../../../components/bluetooth/BluetoothDevice'

import './index.css';

class Main extends React.Component {
  spinner: any;

  componentDidMount() {
    scanForDevices.scan(this.spinner);
  }

  componentWillUnmount() {
  }

  render() {
    return (
      <IonContent class="main-page">
        <IonContent class="header-content">

          <IonHeader class="main-header">
            <IonToolbar class="main-toolbar">
              <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
              <IonIcon icon={refresh} class="refresh-button" onClick={scanForDevices.scan} slot="start"></IonIcon>
              <IonIcon icon={chevronForward} class="next-button" slot="end"></IonIcon>
            </IonToolbar>
          </IonHeader>
        </IonContent>
        <IonContent class="mid-content">
          <IonSpinner name="dots" 
          ref={(spinner) => {this.spinner = spinner}}
          paused={true}
          />
          <IonList class="device-list">
            <IonItem>TestItem</IonItem>
          </IonList>
        </IonContent>
        <IonTabs>
          <IonRouterOutlet></IonRouterOutlet>
          <IonTabBar slot="bottom">
            <IonTabButton tab="Bluetooth">
              <IonIcon icon={wifiOutline}></IonIcon> 
            </IonTabButton>
            <IonTabButton tab="Wifi">
            <IonIcon icon={wifiOutline}></IonIcon>
            </IonTabButton>
          </IonTabBar>
        </IonTabs>
      </IonContent>
    );
  };
};
//<BluetoothDevice.ListItem name={"name"} mac={"hh:FJJF:2df"} connected={false} group={"group"}></BluetoothDevice.ListItem>

class scanForDevices {
  static scan(spinner) {
    this.loadingIcon()
    return this.bluetoothScan(10.0);
   }
  static loadingIcon() {
    this.spinner.paused = false;
   }
  static bluetoothScan(timeout) {
      for(let i = 0; i < 10; i++){
        console.log(new BluetoothDevice("name", "hh:FJJF:2df", false, "group"));
      }
   }
}


export default Main;
