import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonItem, IonIcon, IonSpinner, IonTabs, IonTabBar, IonTabButton, IonRouterOutlet, IonLabel, IonRefresher, IonRefresherContent} from '@ionic/react';
import { refresh, chevronForward, wifiOutline, bluetoothOutline, arrowForwardSharp} from 'ionicons/icons';
import { RefresherEventDetail } from '@ionic/core';

import BluetoothDevice from '../../../components/bluetooth/BluetoothDevice'

import './index.css';

class Main extends React.Component {
  spinner: any;

  componentDidMount() {
    scanForDevices.scan();
  }

  componentWillUnmount() {
  }

  render() {
    return (
      <IonContent class="main-page">
        <IonContent class="header-content">

          <IonHeader class="main-header">
            <IonToolbar class="main-toolbar">
              <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
              <IonIcon icon={arrowForwardSharp} class="next-button" slot="end"></IonIcon>
            </IonToolbar>
          </IonHeader>
        </IonContent>
        <IonContent class="mid-content">
          <IonList class="device-list">
            <IonItem>TestItem</IonItem>
          </IonList>
          <IonRefresher onIonRefresh={scanForDevices.scan} slot="fixed">
              <IonRefresherContent
                pullingText="Pull to refresh"
                refreshingSpinner="circles"
                refreshingText="Refreshing...">
              </IonRefresherContent>
          </IonRefresher>
        </IonContent>
        <IonTabs>
          <IonRouterOutlet></IonRouterOutlet>
          <IonTabBar slot="bottom" class="tab-bar">
            <IonTabButton tab="Wifi" href="/scan-devices/wifi">
              <IonIcon icon={wifiOutline} class="tab-bar-info"></IonIcon> 
              <IonLabel class="tab-bar-info">Wifi</IonLabel>
            </IonTabButton>
            <IonTabButton tab="Bluetooth" href="/scan-devices/bluetooth">
              <IonIcon icon={bluetoothOutline} class="tab-bar-info"></IonIcon>
              <IonLabel class="tab-bar-info">Bluetooth</IonLabel>
            </IonTabButton>
          </IonTabBar>
        </IonTabs>
      </IonContent>
    );
  };
};
