class scanForDevices {
  static scan() {
    this.loadingIcon()
    return this.bluetoothScan(10.0);
  }
  static loadingIcon() {
    console.log("scanning!");
  }
  static bluetoothScan(timeout) {
    for(let i = 0; i < 10; i++){
      //console.log(new BluetoothDevice("name", "hh:FJJF:2df", false, "group"));
    }
  } 
}


export default Main;
