"use strict";

import React, { useState, useEffect } from 'react';
import { IonIcon, IonSpinner, IonItem, IonAvatar, IonLabel } from '@ionic/react';
import { bluetoothOutline, checkmarkOutline, closeOutline } from 'ionicons/icons';

import './BluetoothDeviceItem.css';
import { BluetoothDevice } from '../../interfaces/BluetoothDevice'

interface Props {
  name: string;
  mac: string;
}

export const BluetoothDeviceItem = (Props) => {
  const [ connectingState, setConnectingState ] = useState('not_connected');

  const connenct = async () => {

    setConnectingState('connecting');

    try { await tryConnect(); } 
    catch(error) { }

    setConnectingState('connected');


  }

  const stateChangeCallback = () => {
    let connectingIcon = document.getElementsByClassName("connecting-icons")[0];
    let spinnerOpacity = document.getElementsByClassName("connecting-spinner")[0]["style"].opacity;

    switch(connectingState) {
      case 'not_connected':
        connectingIcon["style"]["color"] = "var(--ion-color-danger)";
        connectingIcon["icon"] = closeOutline;
        break;
      case 'connecting':
        spinnerOpacity = 1;
        connectingIcon["style"]["opacity"] = 0;
        break;
      case 'connected':
        spinnerOpacity = 0;
        connectingIcon["style"]["opacity"] = 1;
        connectingIcon["style"]["color"] = "var(--ion-color-success)";
        connectingIcon["icon"] = checkmarkOutline;
    }
  }

  const tryConnect = () => {
    return new Promise(function(resolve, reject) {
      console.log("connecting...");
      setTimeout(function(){ resolve(1); }, 1000);
    });
  }

  useEffect(() => {
    stateChangeCallback();
  }, [connectingState]);

  return (
    <IonItem lines="none" class="item-container" onClick={connenct}>
      <IonAvatar slot="start" class="bluetooth-icon-container">
        <IonIcon icon={bluetoothOutline} class="bluetooth-icon"></IonIcon>
      </IonAvatar>
      <IonAvatar slot="end" class="connecting-icons-container">
        <IonIcon icon={closeOutline} class="connecting-icons"></IonIcon>
        <IonSpinner name="dots" class="connecting-spinner"></IonSpinner>
      </IonAvatar>
      <IonLabel>
        <div className="device-name">{Props.name}</div>
        <div className="device-mac">{Props.mac}</div>
      </IonLabel>
    </IonItem>
  );

}
export default BluetoothDeviceItem;


