import { IonList } from "@ionic/react";
import React, { useState, useEffect } from "react";

interface Props {
    devices: any[]
}

class BluetoothDeviceList extends React.Component<Props, {}> {

    constructor(Props) {
        super(Props);
    }

    componentDidUpdate(prevProps) { //if a new set of devices is passed as props
        if(prevProps.devices !== this.props.devices) {
            this.forceUpdate();
        }
    }

    render() {
        return (
            <IonList class="device-list">
                {
                    this.props.devices.map(el => el)
                }
            </IonList>
        );
    }
}
export default BluetoothDeviceList;
