class BluetoothDeviceItem extends React.Component<Props, {}> {
	constructor(props) {
		super(props);
	}

	private updateIcons = () => {
		const elementIndex = Array.prototype.slice.call(document.getElementsByClassName("device-list")[0].children).findIndex((data) => {return data.id === this.props.mac});

		const connectingIcon = document.getElementsByClassName("connecting-icons")[elementIndex];
		const spinnerIcon = document.getElementsByClassName("connecting-spinner")[elementIndex];

		console.log(connectingIcon);

		switch (this.props.state()) {
			case 'not_connected':
				spinnerIcon.innerHTML = "";
				//connectingIcon["style"]["color"] = "var(--ion-color-danger)";
				connectingIcon.innerHTML = "not connected"//ReactDOMServer.renderToString(<IonIcon icon={closeOutline}></IonIcon>);
				break;
			case 'connecting':
				spinnerIcon.innerHTML =  "connecting"//ReactDOMServer.renderToString(<IonSpinner name="dots" ></IonSpinner>);
				//connectingIcon["style"]["opacity"] = 0;
				break;
			case 'connected':
				spinnerIcon.innerHTML = "";
				//connectingIcon["style"]["opacity"] = 1;
				//connectingIcon["style"]["color"] = "var(--ion-color-success)";
				connectingIcon.innerHTML = "connected"//ReactDOMServer.renderToString(<IonIcon icon={checkmarkOutline}></IonIcon>);
				break;
		}
		console.log(connectingIcon);
	}

	public rerender = () => {
		this.updateIcons();
	}

	private wait = async () => {
		this.props.onClick(this.props.mac);
		this.rerender();
	}

	render() {
		return (
			<IonItem lines="none" class="item-container" id={this.props.mac} onClick={this.wait} >
				<IonAvatar slot="start" class="bluetooth-icon-container">
					<div className="bluetooth-icon">
						<IonIcon icon={bluetoothOutline} ></IonIcon>
					</div>
				</IonAvatar>
				<IonAvatar slot="end" class="connecting-icons-container">
					<div className="connecting-icons">
						<IonIcon icon={closeOutline}></IonIcon>
					</div>
					<div className="connecting-spinner">
						<IonSpinner name="dots" ></IonSpinner>
					</div>
				</IonAvatar>
				<IonLabel>
					<div className="device-name">{this.props.name}</div>
					<div className="device-mac">{this.props.mac}</div>
				</IonLabel>
			</IonItem>
		);
	}
}

export default BluetoothDeviceItem;
