import { IonList } from "@ionic/react";
import React, { useState, useEffect } from "react";

interface Props {
    devices: any[]
}

const BluetoothDeviceList = Props => {

    const [devices, setDevices] = useState(Props.devices);
    useEffect(() => {
		onListChange();
    }, [devices]);

    const onListChange = () => {
        return (
            <IonList>
                {
                    devices.map(el => el)
                }
            </IonList>
        );
    }

	return (
		<IonList class="device-list">
			{
				devices.map(el => el)
			}
		</IonList>
	);
}
export default BluetoothDeviceList;
