import React from 'react';
import { IonIcon, IonSpinner, IonItem, IonAvatar, IonLabel, IonItemDivider } from '@ionic/react';
import { bluetoothOutline, checkmarkOutline, closeOutline, ellipsisVertical } from 'ionicons/icons';

import './BluetoothDeviceItem.css';
import { BluetoothDevice } from '../../interfaces/BluetoothDevice'

interface Props {
  device: BluetoothDevice;
}

class BluetoothDeviceItem extends React.Component {

  componentDidMount() {
  }

  render() {
    return (
    <IonItem lines="none" class="item-container"  onClick={() => this.connenct()}>
      <IonAvatar slot="start" class="bluetooth-icon-container">
        <IonIcon icon={bluetoothOutline} class="bluetooth-icon"></IonIcon>
      </IonAvatar>
      <IonAvatar slot="end" class="connecting-icons-container">
        <IonIcon icon={closeOutline} class="connecting-icons"></IonIcon>
        <IonSpinner name="dots" class="connecting-spinner"></IonSpinner>
      </IonAvatar>
      <IonAvatar slot="end" class="group-icon-container">
        <IonIcon icon={ellipsisVertical} class="group-icon"></IonIcon>
      </IonAvatar>
      <IonLabel>
        <div className="device-name">this-is-a-really-long-device-name-for-some-reason</div>
        <div className="device-mac">00:04:ab:8f:40:hg</div>
      </IonLabel>
    </IonItem>
    );
  }

  private connenct = async () => {
    const ic = document.getElementsByClassName("connecting-icons")[0];
    const sp = document.getElementsByClassName("connecting-spinner")[0];

    sp["style"]["opacity"] = 1;
    ic["style"]["opacity"] = 0;

    this.tryConnect();

    sp["style"]["opacity"] = 0;
    ic["style"]["opacity"] = 1;
    ic["style"]["color"] = "var(--ion-color-success)";
    ic["icon"] = checkmarkOutline;

  }

  private tryConnect() {
    setTimeout(function(){ console.log("connecting..."); return; }, 5000);
  }
}

export default BluetoothDeviceItem;


