import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent } from '@ionic/react';

import Typed from 'react-typed';

import './index.css';

interface State {
  typed: Typed;
}

class Main extends React.Component {

  state = {
    typed: "",
  }

 componentDidMount() {
  this.state.typed.start(); //start the typing on load
 }

  render() {
    return (
      <IonContent class="main_page">
        <IonHeader class="main-header">
          <IonToolbar class="main-toolbar">
            <IonTitle class="main-toolbar-title" size="large">Looking for devices
              <Typed
                typedRef={(typed) => { this.typed = typed; }}
                strings={['...']}
                typeSpeed={160}
                backSpeed={160}
                backDelay={160}
                startDelay={160}
                showCursor={false}
                loop
                />
            </IonTitle>
          </IonToolbar>
        </IonHeader>

      </IonContent>
    );
  };
};


class scanForDevices {
  static thisIsAFunction() {
    console.log("loaded!1");
   }
}


export default Main;
