import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonItem, IonIcon, IonAlert, IonTabs, IonTabBar, IonTabButton, IonRouterOutlet, IonLabel, IonRefresher, IonRefresherContent, IonSpinner} from '@ionic/react';
import { refresh, chevronForward, wifiOutline, bluetoothOutline, thumbsUpSharp } from 'ionicons/icons';

import BluetoothDeviceItem from '../../../components/bluetooth/BluetoothDeviceItem';
import { BluetoothDevice } from '../../../interfaces/BluetoothDevice';

import './index.css';

interface State {
  device: BluetoothDevice;
  deviceList: BluetoothDevice[];
}

class Main extends React.Component {
  spinner: any;

  componentWillMount() {
  }

  componentDidMount() {
    scanForDevices.scan(this.spinner);
  }

  componentWillUnmount() {
  }

  render() {
    return (
      <IonContent class="main-page">
        <IonContent class="header-content">
          <IonHeader class="main-header">
            <IonToolbar class="main-toolbar">
              <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
              <IonIcon icon={chevronForward} class="next-button" slot="end" ref={spinner => {this.spinner = spinner}}></IonIcon>
            </IonToolbar>
          </IonHeader>
          <IonSpinner class="single-refresh-spinner"></IonSpinner>
        </IonContent>
        <IonContent class="mid-content">
          {/*<IonList class="device-list">
            <IonItem>TestItem</IonItem>
          </IonList>*/}
          <IonRefresher onIonRefresh={scanForDevices.scan} slot="fixed">
              <IonRefresherContent
                pullingText="Pull to refresh"
                refreshingText="Refreshing...">
              </IonRefresherContent>
          </IonRefresher>
        </IonContent>
        <IonTabs>
          <IonRouterOutlet></IonRouterOutlet>
          <IonTabBar slot="bottom" class="tab-bar">
            <IonTabButton tab="Wifi">
              <IonIcon icon={wifiOutline} class="button-wifi"></IonIcon> 
              <IonLabel class="button-wifi button-wifi-label">Wifi</IonLabel>
              <IonContent class="button-wifi-background"></IonContent>
            </IonTabButton>
            <IonTabButton tab="Bluetooth" href="/scan-devices/bluetooth">
              <IonIcon icon={bluetoothOutline} class="button-bluetooth"></IonIcon>
              <IonLabel class="button-bluetooth button-bluetooth-label">Bluetooth</IonLabel>
              <IonContent class="button-bluetooth-background"></IonContent>
            </IonTabButton>
          </IonTabBar>
        </IonTabs>
        {/*<IonAlert
          isOpen={true}
          header={'Continue?'}
          message={'Are you sure you want to continue without selecting a device?'}
          buttons={['Cancel', 'Continue']}
        />*/}
      </IonContent>
    );
  };
};
//<BluetoothDevice.ListItem name={"name"} mac={"hh:FJJF:2df"} connected={false} group={"group"}></BluetoothDevice.ListItem>

class scanForDevices {

  static scan(spinner) {
    return this.bluetoothScan(10, spinner);
  }
  static bluetoothScan(timeout, spinner) {
    spinner.style.opacity = 0;
  } 
}


export default Main;
