import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent } from '@ionic/react';

import Typed from 'react-typed';

import './index.css';

interface State {
  name: string;
  ip: string;
  mac: string;
  connected: boolean;
}

class Main extends React.Component {
  typed: any;

 componentDidMount() {
  this.typed.start(); //start the typing on load
 }

 componentWillUnmount() {
   this.typed.destroy(); //destroy object on unload
 }

  render() {
    return (
      <IonContent class="main_page">
        <IonContent class="content_header">
          <IonHeader class="main-header">
            <IonToolbar class="main-toolbar">
              <IonTitle class="main-toolbar-title" size="large">Looking for devices
                <Typed
                  typedRef={(typed) => { this.typed = typed; }}
                  strings={['...']}
                  typeSpeed={160}
                  backSpeed={160}
                  backDelay={160}
                  startDelay={160}
                  showCursor={false}
                  loop
                  />
              </IonTitle>
            </IonToolbar>
          </IonHeader>
        </IonContent>
      </IonContent>
    );
  };
};


