import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonItem, IonIcon, IonSpinner, IonTabs, IonTabBar, IonTabButton, IonRouterOutlet, IonLabel, IonRefresher, IonRefresherContent} from '@ionic/react';
import { refresh, chevronForward, wifiOutline, bluetoothOutline, arrowForwardSharp} from 'ionicons/icons';
import { RefresherEventDetail } from '@ionic/core';

import BluetoothDevice from '../../../components/bluetooth/BluetoothDevice'

import './index.css';

class Main extends React.Component {
  spinner: any;

  componentDidMount() {
    scanForDevices.scan();
  }

  componentWillUnmount() {
  }

  render() {
    return (
      <IonContent class="main-page">
        <IonContent class="header-content">

          <IonHeader class="main-header">
            <IonToolbar class="main-toolbar">
              <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
              <IonIcon icon={arrowForwardSharp} class="next-button" slot="end"></IonIcon>
            </IonToolbar>
          </IonHeader>
        </IonContent>
        <IonContent class="mid-content">
          <IonRefresher onIonRefresh={scanForDevices.scan}>
              <IonRefresherContent refreshingSpinner="dots">
                <IonList class="device-list">
                  <IonItem>TestItem</IonItem>
                </IonList>
            </IonRefresherContent>
        </IonRefresher>
        </IonContent>
        <IonTabs>
          <IonRouterOutlet></IonRouterOutlet>
          <IonTabBar slot="bottom" class="tab-bar">
            <IonTabButton tab="Bluetooth">
              <IonIcon icon={wifiOutline}></IonIcon> 
              <IonLabel>Wifi</IonLabel>
            </IonTabButton>
            <IonTabButton tab="Wifi">
              <IonIcon icon={bluetoothOutline}></IonIcon>
              <IonLabel>Bluetooth</IonLabel>
            </IonTabButton>
          </IonTabBar>
        </IonTabs>
      </IonContent>
    );
  };
};
//<BluetoothDevice.ListItem name={"name"} mac={"hh:FJJF:2df"} connected={false} group={"group"}></BluetoothDevice.ListItem>

class scanForDevices {
  static scan() {
    this.loadingIcon()
    return this.bluetoothScan(10.0);
   }
  static loadingIcon() {
   }
  static bluetoothScan(timeout) {
      for(let i = 0; i < 10; i++){
        console.log(new BluetoothDevice("name", "hh:FJJF:2df", false, "group"));
      }
   }
}


export default Main;
