import React from 'react';
import { IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonItem, IonIcon, IonSpinner} from '@ionic/react';
import { refresh, chevronForward } from 'ionicons/icons';

import BluetoothDevice from '../../components/bluetooth/BluetoothDevice'

import './index.css';
import '../../theme/fonts.css';

class Main extends React.Component {
  spinner: any;

  componentDidMount() {
    scanForDevices.scan();
  }

  componentWillUnmount() {
  }

  render() {
    return (
      <IonContent class="main-page">
        <BluetoothDevice name="hello" mac="fff" type="fff" connected={false}>
        </BluetoothDevice>
        <IonContent class="header-content">
          <IonHeader class="main-header">
            <IonToolbar class="main-toolbar">
              <IonTitle class="main-toolbar-title" size="large">Select A Device</IonTitle>
              <IonIcon icon={refresh} class="refresh-button" onClick={scanForDevices.scan}></IonIcon>
              <IonIcon icon={chevronForward} class="next-button"></IonIcon>
            </IonToolbar>
          </IonHeader>
        </IonContent>
        <IonContent class="mid-content">
          <IonSpinner name="dots" 
          ref={(spinner) => {this.spinner = spinner}}
          paused={true}
          />
          <IonList class="device-list">
            <IonItem>TestItem</IonItem>
          </IonList>
        </IonContent>
      </IonContent>
    );
  };
};


class scanForDevices {
  static scan() {
    this.loadingIcon()
    return this.bluetoothScan(10.0);
   }
  static loadingIcon() {
        //Main.spinner.paused
   }
  static bluetoothScan(timeout) {

   }
}


export default Main;
